import Task from "../Task/Task"
import { useSelector } from "react-redux";
import { statusFilters } from "../../Redux/constants";

const statusFilter = useSelector(state => state.filter?.status);


const getVisibleTasks = (tasks, statusFilters) => {
    switch(statusFilters) {
        case statusFilters.active:
            return tasks.filter(task => !task.completed)
        case statusFilters.completed: 
            return tasks.filter(task => task.completed)
        default: 
        return tasks
    }
}

const TaskList = () => {

const tasks = useSelector(state => state.tasks)
const statusFilters = useSelector(state => state.filter.status)
const visibleTasks = getVisibleTasks(tasks, statusFilters)

return (
    <ul>
        {[].map(task => (
            <li key={task.id}>
                 <Task task={task}/>
            </li>
        ))}
    </ul>
)

}

export default TaskList
