import { createStore } from "redux";
import { devToolsEnhancer } from "@redux-devtools/extension";


const initialState = {
    tasks: [
        {id: 0, text: '1'},
        {id: 1, text: '2'},
        {id: 2, text: '3'},
    ],
    filters: {
        status: 'all',
    },
}

const rootReducer = (state = initialState, action) => {
    return state
}

export const store = createStore(rootReducer)
